import React, {Component} from 'react';
import PopUp from "./PopUp";
import TrappistPopUp from "./TrappistPopUp";
import ComparePopUp from "./ComparePopUp";
import ReactDOM from 'react-dom';
import './index.css';
import Congrats from "./congrats"




class Home extends Component {
    state = {
        cal_open: false,
        contacts_open: false,
        notes_open: false,
        cal: "https://raw.githubusercontent.com/jgraves123/happy-xmas-chris/master/images/calendar.png?raw=true",
        contacts: "https://raw.githubusercontent.com/jgraves123/happy-xmas-chris/master/images/contacts.png?raw=true",
        notes: "https://raw.githubusercontent.com/jgraves123/happy-xmas-chris/master/images/notes.png?raw=true",
        intro: "https://raw.githubusercontent.com/jgraves123/happy-xmas-chris/master/images/intro-letter.png?raw=true",
        path: "https://raw.githubusercontent.com/jgraves123/happy-xmas-chris/master/images/path.jpeg?raw=true",
        colors: ["red", "blue", "green", "yellow", "orange", "purple"],
        cir1: 0,
        cir2: 0,
        cir3: 0,
        cir4: 0,
    };


    calPop = () => {
        this.setState({
            cal_open: !this.state.cal_open
        });
    };

    contactsPop = () => {
        this.setState({
            contacts_open: !this.state.contacts_open
        });
    };

    notesPop = () => {
        this.setState({
            notes_open: !this.state.notes_open
        });
    };


    gliese = () => {
        console.log("why")
        ReactDOM.render(
            <React.StrictMode>
                <Congrats planet="Gliese 876" />
            </React.StrictMode>,
            document.getElementById('root')
        );
    }




    render() {
        return(
            <div>
                <body>
                {/*<Confetti/>*/}
                <header id="main-header">
                    <div className="container">
                        <h1 align='center'>Begin Your Journey</h1>
                    </div>
                </header>
                <nav id="navbar">
                    <div className="container">
                        <ul>
                            <li><a onClick = "window.location.reload();">Home</a></li>
                            <li><a onClick={this.calPop}>Calendar</a></li>
                            <li><a onClick={this.contactsPop}>Contacts</a></li>
                            <li><a onClick={this.notesPop}>Notes</a></li>
                        </ul>
                    </div>
                </nav>
                <div height="400">
                    {/*if state is true, do this pop-up*/}
                    {this.state.cal_open ? <PopUp title="Decemeber 2020" x="1840" y="1130" width="100%" image={this.state.cal} padding="62%" toggle={this.calPop} /> : null}
                    {this.state.contacts_open ? <PopUp title="Contact List" x="744" y="1164" width="60%" image={this.state.contacts} padding="96%" toggle={this.contactsPop} /> : null}
                    {this.state.notes_open ? <PopUp title="My Notes" x="1188" y="1422" width="80%" image={this.state.notes} padding="96%" toggle={this.notesPop} /> : null}
                    {/*<li><a onClick={this.pop_up("Contact List", "744", "1164", this.state.contacts, "50%")}>Contacts</a></li>*/}
                    {/*<li><a onClick={this.pop_up("My Notes", "1188", "1422", this.state.notes, "100%")}>Notes</a></li>*/}
                </div>
                <h2 align="center" className="subheading">Hello Chris, First we need to confirm your identity. </h2>
                <h3 align="center" className="subheading">Please read the documents provided and enter our two factor authorization to continue.</h3>
                <section>
                    <div className="scaling-svg-container" style={{display: "inline-block", width: "50%"}}>
                        {/*resizing*/}
                        <svg className="scaling-svg" viewBox="0 0 994 1400"> {/* Needs auto
                         updating*/}
                            <image x={.1*994} width="80%" href={this.state.intro}/>
                        </svg>
                    </div>
                    <div className="scaling-svg-container" style={{display: "inline-block", width: "50%"}}>
                        <svg className="scaling-svg" viewBox="0 0 994 1400"> {/* Needs auto
                         updating*/}
                            <image x={.1*994} width="80%" href={this.state.path}/>
                        </svg>
                    </div>

            </section>
                <h2 align="center">Two factor identification</h2>
                <section>
                    <div className="scaling-svg-container" align="center" style={{display: "inline-block", width: "50%"}}>
                        {/*resizing*/}
                        <div className="scaling-svg-container" style={{paddingBottom: "20%"}}>
                        <svg className="scaling-svg" viewBox="-20 -20 600 150"> {/* Needs auto
                     updating*/}
                            <circle cx="140" cy="40" r="39" className="click" stroke="white" fill={this.state.colors[this.state.cir1]} onClick={() => this.setState({cir1: (this.state.cir1 +1 )% 6})} />
                            <circle cx="250" cy="40" r="39" className="click" stroke="white" fill={this.state.colors[this.state.cir2]} onClick={() => this.setState({cir1: (this.state.cir2 +1 )% 6})}/>
                            <circle cx="360" cy="40" r="39" className="click" stroke="white"fill={this.state.colors[this.state.cir3]} onClick={() => this.setState({cir1: (this.state.cir3 +1 )% 6})}/>
                            <circle cx="470" cy="40" r="39" className="click" stroke="white" fill={this.state.colors[this.state.cir4]} onClick={() => this.setState({cir1: (this.state.cir4 +1 )% 6})}/>
                        </svg>
                        </div>
                        <button className="button" onClick={this.trappist}><h3>Check</h3></button>
                    </div>
                    <div className="scaling-svg-container" style={{paddingBottom: "10%", display: "inline-block", width: "50%"}}>

                    </div>

                </section>
                    <button className="button" onClick={this.trappist}><h3>TRAPPIST-1</h3></button>
                    <button className="button" onClick={this.gliese}><h3>Gliese 876</h3></button>
                <section id="showcase"><div className="container">
                    <h3 align="center">Lift Off in &nbsp;&nbsp;3...&nbsp;&nbsp; 2...&nbsp;&nbsp; 1...</h3>
                </div></section>

                </body>
                {/*<footer id="main-footer">*/}
                {/*    <h3>Contact info?</h3>*/}
                {/*</footer>*/}
            </div>
        );
    }
}



ReactDOM.render(<Home />, document.getElementById("root"));

export default Home;
