import React, {Component} from 'react';
import PopUp from "./PopUp";
import TrappistPopUp from "./TrappistPopUp";
import ComparePopUp from "./ComparePopUp";
import ReactDOM from 'react-dom';
import './index.css';
import Congrats from "./congrats"




class Home extends Component {
    state = {
        cal_open: false,
        contacts_open: false,
        notes_open: false,
        cal: "https://raw.githubusercontent.com/jgraves123/happy-xmas-chris/master/images/calendar.png?raw=true",
        contacts: "https://raw.githubusercontent.com/jgraves123/happy-xmas-chris/master/images/contacts.png?raw=true",
        notes: "https://raw.githubusercontent.com/jgraves123/happy-xmas-chris/master/images/notes.png?raw=true",
        intro: "https://raw.githubusercontent.com/jgraves123/happy-xmas-chris/master/images/intro-letter.png?raw=true",
        path: "https://raw.githubusercontent.com/jgraves123/happy-xmas-chris/master/images/path.jpeg?raw=true",

    };


    calPop = () => {
        this.setState({
            cal_open: !this.state.cal_open
        });
    };

    contactsPop = () => {
        this.setState({
            contacts_open: !this.state.contacts_open
        });
    };

    notesPop = () => {
        this.setState({
            notes_open: !this.state.notes_open
        });
    };

    pop_up = (title, x, y, image, padding, toggle) => {
        ReactDOM.render(
            <React.StrictMode>
                <PopUp title={title} x={x} y={y} image={image} padding={padding} toggle={toggle}/>
            </React.StrictMode>,
            document.getElementById('root')
        );
    }

    gliese = () => {
        console.log("why")
        ReactDOM.render(
            <React.StrictMode>
                <Congrats planet="Gliese 876" />
            </React.StrictMode>,
            document.getElementById('root')
        );
    }




    render() {
        return(
            <div>
                <body>
                {/*<Confetti/>*/}
                <header id="main-header">
                    <div className="container">
                        <h1 align='center'>Choose your Target Destination</h1>
                    </div>
                </header>
                <nav id="navbar">
                    <div className="container">
                        <ul>
                            <li><a onClick = "window.location.reload();">Home</a></li>
                            <li><a onClick={this.calPop}>Calendar</a></li>
                            <li><a onClick={this.contactsPop}>Contacts</a></li>
                            <li><a onClick={this.notesPop}>Notes</a></li>
                        </ul>
                    </div>
                </nav>
                <div height="400">
                    {/*if state is true, do this pop-up*/}
                    {this.state.cal_open ? <PopUp title="Decemeber 2020" x="1840" y="1130" width="100%" image={this.state.cal} padding="62%" toggle={this.calPop} /> : null}
                    {this.state.contacts_open ? <PopUp title="Contact List" x="744" y="1164" width="60%" image={this.state.contacts} padding="96%" toggle={this.contactsPop} /> : null}
                    {this.state.notes_open ? <PopUp title="My Notes" x="1188" y="1422" width="80%" image={this.state.notes} padding="96%" toggle={this.notesPop} /> : null}
                    {/*<li><a onClick={this.pop_up("Contact List", "744", "1164", this.state.contacts, "50%")}>Contacts</a></li>*/}
                    {/*<li><a onClick={this.pop_up("My Notes", "1188", "1422", this.state.notes, "100%")}>Notes</a></li>*/}
                </div>
                <section>
                    <div className="scaling-svg-container" style={{paddingBottom: "120%", display: "inline-block", width: "42%"}}>
                        {/*resizing*/}
                        <svg className="scaling-svg" viewBox="0 0 994 1400"> {/* Needs auto
                         updating*/}
                            <image x={.3*994} width="40%" href={this.state.intro}/>
                        </svg>
                    </div>
                    <div className="scaling-svg-container" style={{paddingBottom: this.props.padding, display: "inline-block", width: "42%"}}>
                        <svg className="scaling-svg" viewBox="0 0 3024 3860"> {/* Needs auto
                         updating*/}
                            <image x={.3*3024} width="40%" href={this.state.path}/>
                        </svg>
                    </div>

                </section>
                <div align="center">
                    <h2>Time to make your decision. Where will you travel to?</h2>
                    <button className="button" onClick={this.trappist}><h3>TRAPPIST-1</h3></button>
                    <button className="button" onClick={this.gliese}><h3>Gliese 876</h3></button>
                </div>
                <section id="showcase"><div className="container">
                    <h3 align="center">Lift Off in &nbsp;&nbsp;3...&nbsp;&nbsp; 2...&nbsp;&nbsp; 1...</h3>
                </div></section>

                </body>
                {/*<footer id="main-footer">*/}
                {/*    <h3>Contact info?</h3>*/}
                {/*</footer>*/}
            </div>
        );
    }
}



ReactDOM.render(<Home />, document.getElementById("root"));

export default Home;
